import { reactive, nextTick } from 'vue';



export function tableBean(service, axiosConfig) {
  
  const state = reactive({
    editedRow: null,
    selectedRow: null,
    list: [],
    isDisabled: false,
  });




  const list = async () => {
    console.log('Entrando en list de tableBean')
    try {
      const response = await service.getAll(axiosConfig);
      state.list = response.data;

    } catch (error) {
      console.log(error);
    }
  };
  // Llamamos a la función list() al exportar la función tableBean
  list();

//***CREATE************/
  const create = () => {
    state.isDisabled = false;
    state.editedRow = null;
  };
//***SAVE*************/
  const save = async () => {
    console.log('Dentro del save del composable')
    console.log(state.editedRow);
    try {
      this.service.save(this.localEditedRow, this.axiosConfig).then(() => {
        this.$toast.add({
          severity: 'success',
          summary: 'Guardando',
          detail: 'REGISTRO CREADO CORRECTAMENTE. GUARDADO EN BASE DE DATOS',
          life: 3000
        });
        this.getAll();
        this.isDisabled = true;
      }).catch((error) => {
        console.log('primer catch');
        console.log(error);
        if (error.response.status === 400) {
          const errorMessage = error.response.data;
          this.$toast.add({
            severity: 'error',
            summary: 'Error',
            detail: errorMessage,
            life: 3000
          });
        }
      });
    } catch (error) {
      console.log('segundo catch');
      console.log(error);
      this.$toast.add({
        severity: 'error',
        summary: 'Error',
        detail: 'No se pudo guardar el registro. Por favor, inténtalo de nuevo más tarde.',
        life: 3000
      });
    }

  };






  const remove = async (rowToDelete) => {
    try {
      await service.remove(rowToDelete.id, axiosConfig);
      state.list = await list();
      state.isDisabled = true;
    } catch (error) {
      console.log(error);
    }
  };

  return {
    state,
    save,
    remove,
    create,
    list,
    setEditedRow,
    rowSelectListener

  };
}
