import Vue from 'vue';

// Definir el decorador personalizado @RowSelect
Vue.decorator('RowSelect', (options, key) => {
  // Obtener el método original
  const originalMethod = options.methods[key];

  // Reemplazar el método original con una función que llame al método selectRow del mixin y emita un evento personalizado
  options.methods[key] = function(row) {
    // Llamar al método selectRow del mixin
    this.selectRow(row);

    // Obtener el nombre del método anotado con @RowSelect
    const methodName = this.$options[`${key}RowSelectMethod`];

    // Llamar al método anotado con @RowSelect si existe
    if (methodName) {
      this[methodName](row);
    }

    // Llamar al método original si existe
    if (originalMethod) {
      originalMethod.call(this, row);
    }
  }
});
