// MyComponent.vue

<template>
  <div>
    <p>Valor actual de 'nombre': {{ nombre }}</p>
  </div>
</template>

<script>
import TableBean from '@/service/TableBean';

export default {
  name: 'MyComponent',
  mixins: [TableBean],
  methods: {
    // Define un método anotado con @RowSelect
    '@RowSelect': function () {
      console.log('Rowselect del componente ');
      console.log('Nombre en el componente: ', this.$data.nombre);
      console.log()
      // Lógica del método anotado con @RowSelect
      // ...
    }
  },
  updated() {
    const rowSelectMethod = this.$options.methods['@RowSelect'];
    if (typeof rowSelectMethod === 'function') {
      // Ejecuta el método @RowSelect en el componente
      rowSelectMethod();
    }
  },
  mounted() {
    // Llama al método rowSelectListener del mixin en el evento 'mounted'
    console.log('Nombre en el componente: ', this.nombre);
    this.rowSelectListener();
  }
}


</script>
