import { ref } from 'vue';

export const TableBean = {
  data() {
    return {
      selectedRow: ref({ id: 0, nombre: '', descripcion: '' })
    };
  },
  methods: {
    rowSelectListener() {
      console.log('Selected row:', this.selectedRow.value);
      const methods = this.$options.methods;
      for (const methodName in methods) {
        const method = methods[methodName];
        if (method?.rowSelect) {
          method.rowSelect();
        }
      }
    },
  },
};
