import { useStore } from 'vuex';
import { tableBean } from '@/composables/tableBean';
import PedidoService from '@/service/PedidoService';
import EstadoService from '@/service/EstadoService';
import { computed, ref, provide } from 'vue';
import GenericDatatable from './GenericDatatable.vue';
export default {
  components: {
    GenericDatatable
  },
  setup() {
    const axiosConfig = computed(() => store.getters.axiosConfig);
    const store = useStore();
    const pedidoService = new PedidoService();
    const estadoService = new EstadoService();
    const {
      state,
      list,
      create,
      save,
      remove,
      rowSelectListener
    } = tableBean(pedidoService, axiosConfig.value);
    const header = "Lista de Pedidos";
    const crudColumns = [{
      title: 'ID',
      field: 'id'
    }, {
      title: 'Nº de seguimiento',
      field: 'numSeguimiento'
    }, {
      title: 'Método de pago',
      field: 'metodoDePago'
    }, {
      title: 'Estado',
      field: 'estado.nombre'
    }, {
      title: 'Precio total',
      field: 'precioTotal'
    }, {
      title: 'Dirección de envío',
      field: 'direccionEnvio'
    }, {
      title: 'Observaciones',
      field: 'observaciones'
    }, {
      title: 'Fecha del pedido',
      field: 'fechaPedido'
    }];
    /**Obtener los estados para el selectOnemenu de estados */
    const estados = ref([]);
    const getEstados = async () => {
      const response = await estadoService.getAll(axiosConfig.value);
      estados.value = response.data;
    };
    if (estados.value.length === 0) {
      getEstados();
    }
    const rowSelectListenerPedidos = async selectedRow => {
      console.log('rowselect del padre', selectedRow);
      await rowSelectListener(selectedRow);
      console.log('ha vuelto al rowselect del padre');
    };
    const beforeSave = async editedRow => {
      console.log('beforeSave del padre', editedRow);
      await save(editedRow);
      console.log('ha vuelto al beforeSave del padre');
    };
    const beforeCreate = () => {
      console.log('beforeCreate del padre');
      create();
      console.log('ha vuelto al beforeCreate del padre');
    };
    const beforeDelete = async editedRow => {
      console.log('beforeDelete del padre', editedRow);
      await remove(editedRow);
      console.log('ha vuelto al beforeDelete del padre');
    };
    provide('rowSelectListenerPedidos', rowSelectListenerPedidos);
    provide('beforeSave', beforeSave);
    provide('beforeCreate', beforeCreate);
    provide('beforeDelete', beforeDelete);
    return {
      state,
      list,
      crudColumns,
      rowSelectListener,
      header,
      save,
      remove,
      create,
      estados
    };
  }
};
