import { reactive, nextTick } from 'vue';


const state = reactive({
  editedRow: null,
  selectedRow: null,
  list: [],
  isDisabled: false,
});
export function tableBean(service, axiosConfig) {

  const setEditedRow = (newValue) => {
    state.editedRow = newValue
  }


  const create = () => {
    state.isDisabled = false;
    state.editedRow = null;
  };

  const list = async () => {
    console.log('Entrando en list de tableBean')
    try {
      const response = await service.getAll(axiosConfig);
      state.list = response.data;
      console.log(state.list);
    } catch (error) {
      console.log(error);
    }
  };

  // Llamamos a la función list() al exportar la función tableBean
  list();

  const save = async () => {
    console.log('Dentro del save del composable')
    console.log(state.editedRow);

  };

  const rowSelectListener = async (localSelectedRow) => {
    console.log('Dentro del rowSelectListener del composable');
    state.selectedRow = { ...localSelectedRow }; // Asigna una copia del objeto
    await nextTick(); // Espera a que se actualice el valor de state.selectedRow
  
    console.log('SelectedRow: ', state.selectedRow);
    // Aquí puedes modificar el valor de selectedRow
  };
  




  const remove = async (rowToDelete) => {
    try {
      await service.remove(rowToDelete.id, axiosConfig);
      state.list = await list();
      state.isDisabled = true;
    } catch (error) {
      console.log(error);
    }
  };

  return {
    state,
    save,
    remove,
    create,
    list,
    setEditedRow,
    rowSelectListener

  };
}
