import { ref, onBeforeUnmount } from 'vue';
import { findComponentRoot } from 'vue';

export default {
  setup() {
    // Define un objeto ref con la propiedad 'nombre'
    const nombre = ref('Soy una variable del mixin');

    // Define un método 'rowSelectListener'
    const rowSelectListener = () => {
      // Lógica del método rowSelectListener
      // ...
      console.log('Rowselect del mixin');
      
      // Modifica la propiedad 'nombre'
      nombre.value = '¡La variable del mixin ha sido modificada!';

      // Busca el componente que utiliza este mixin
      const componentRoot = findComponentRoot(rowSelectListener);
      if (componentRoot) {
        // Busca todos los métodos anotados con @RowSelect en el componente
        const rowSelectMethods = Object.keys(componentRoot.__vue__.$options.methods || {})
          .filter(key => key.startsWith('@RowSelect'))
          .map(key => componentRoot.__vue__.$options.methods[key]);
        
        // Ejecuta todos los métodos encontrados
        rowSelectMethods.forEach(method => method());
      }
    };

    // Llama al método 'rowSelectListener' en el evento 'beforeUnmount'
    onBeforeUnmount(() => {
      rowSelectListener();
    });

    // Exporta el objeto ref y el método 'rowSelectListener'
    return {
      nombre,
      rowSelectListener,
    };
  },
};
