import { reactive } from 'vue';

export function useTableBean(service, axiosConfig) {
  const state = reactive({
    editedRow: null,
    selectedRow: null,
    list: [],
    isDisabled: false,
  });

  const create = () => {
    state.isDisabled = false;
    state.editedRow = null;
  };

  const getAll = async () => {
    try {
      const response = await service.getAll(axiosConfig);
      state.list = response.data;
    } catch (error) {
      console.log(error);
    }
  };

  const save = async (updatedRow) => {
    console.log(updatedRow);
    updatedRow = {
      id: 2,
      estado: "suspenso",
      productos: [],
      precioTotal: 15,
      direccionEnvio: "otro lado",
      metodoDePago: "paypal",
      numSeguimiento: "otro",
      fechaCreacion: new Date(),
      observaciones: "Primer editedRow al composable",
    };

    
  };

  const remove = async (rowToDelete) => {
    try {
      await service.remove(rowToDelete.id, axiosConfig);
      state.list = await getAll();
      state.isDisabled = true;
    } catch (error) {
      console.log(error);
    }
  };

  return {
    ...state,
    create,
    getAll,
    save,
    remove,
  };
}
